package Lec4;

public class Pattern_29 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		int nst=n;
		int row=1;
		while(row<=n) {
			int cst=1;
			int val=5;
			while(cst<=nst) {
				System.out.print( val+" ");
				val--;
				cst++;
			}
			System.out.println();
			row++;
		
			
			
		}
			
			
			
		

	}

}
